changeSet.sql:
CREATE TABLE fridge
(
    id   BIGSERIAL PRIMARY KEY NOT NULL,
    name VARCHAR(255)          NOT NULL,
);

CREATE TABLE product
(
    id        BIGSERIAL PRIMARY KEY         NOT NULL,
    fridge_id BIGINT REFERENCES fridge (id) NOT NULL,
    type      VARCHAR(50)                   NOT NULL,
    count     INTEGER                       NOT NULL
);
